import {PRODUCT_LOGIN_REQUEST, PRODUCT_LOGIN_SUCCESS, PRODUCT_LOGIN_FAIL, USER_LOGOUT} from "../constants/userConstants"

export const userLoginReducer = (state = {}, action) =>{
    switch(action.type){
       case PRODUCT_LOGIN_REQUEST:
           return {loading:true }
       case PRODUCT_LOGIN_SUCCESS:
           return {loading: false, userInfo: action.payload}
       case PRODUCT_LOGIN_FAIL:
           return {loading: false, error: action.payload}
        case USER_LOGOUT:
            return {}
       default:
           return state
    }
   }
